function playStep(step){
	//-------------------------------------------------------------------------------------
	// Put code here that runs when each step is entered.
	switch (step) {
		case 0:
			reset();
			break;
		case 1:
			reset();
			break;
		case 2:
			reset();
			break;
		case 3:
			reset();
			break;
		case 4:
			reset();
			break;
		case 5:
			reset();
			break;
		default:
			reset();
			break;
	}

	//-------------------------------------------------------------------------------------
}
